import React from 'react';
import { useState } from 'react';
//useState : in react usestate is know as hook 


 // array destructuring : = this means that cut the array in small small bits to use it in simple way 
 //example: const arrname =['vi,'ki','kio'] ; this is array of name arrname .. if we have to acces first valeu
 // of arrname array => name[0] acces by index no. 
 // using destructring : const [value1 , value2 , value3] = arrname; here the value is store inside the
 // value1,2,3 variable .. whwn we want acces value we have to only pass variable name ( name1 = vi )
 // name1 is in arrname array  so vi in assign to name1 varaible ..
const App= ()=> {
    
 
const state = useState(); //  when we use usestate we get an array which return 2 value 1st(undefined)
//and 2nd(function)  .. 1st value is current data and 2nd value is updateed function or data  
//(when we give a value inside the usestate which is known as inital data )

//console.log(state);

const[count,setCount] = useState(0); // this is same as destruturing ('usestate()' is an arrray inside this we pass 
// count as array elemnt ... 

const InNum = ()=>{
setCount(count +1);

};





return(
    <>
    <h1>{count}</h1>
    <button onClick={InNum} >click me</button>
    </>


);

};
export default App;
