import React, { useState } from 'react';

// react work on control components 
// onchange only work on input type
// we cann give same event more time
const App = ()=>{


// we can pass a object inside the useState (call function  inside it)

const [fullname , setfullname] = useState({
    firstname: "",
    lastname: "",
}
); // this is for name

// const[lastname , setlastname] = useState("");// this is for last name

// const [fullname , setfullname] = useState(""); // this is for fullname which display after clicking on button

// const [fullname2 , setfullname2] = useState("");

const inputevent = (event) =>{
    console.log(event.target.value);// show a name(word)value one by one in console
    console.log(event.target.name); // show name attribute 

    const value = event.target.value;
    const name = event.target.name;

    setfullname((prevalue)=>{
    // console.log( prevalue);
    if(name === 'firstname'){
        return{
            firstname: value,
            lastname: prevalue.lastname,
    
        };
       }else
       if(name === 'firstname'){
        return{
            firstname: prevalue.firstname,
            lastname: value,
    
        };
       }
        
    }); 
};

// const inputevent1 = (event) => {
// setlastname(event.target.value);
// }

const onsubmit = (event) =>{
event.preventDefault();
alert("form is submitted");
//it prevent it default behavior(in shor we dont want default )that why he dont refresh page 
    // setfullname(name);
    // setfullname2(lastname);
};


    return(
    <>
    <form onSubmit={onsubmit}>
    <div>
    <h1>Hello {fullname.firstname}{fullname.lastname} </h1>        

    <input type="text" placeholder="Enter name"  onChange={inputevent}
    // value={fullname.firstname}
     name="firstname"
     />

    <br />

    <input type="text" placeholder="Enter lastname"  onChange={inputevent}
    value={fullname.lastname}
    name="lastname"
    />

    <button type="submit">click me </button>
    </div>
    </form>

    </>
    );
};


export default App;
