import React, { useState } from 'react';
import Todolist from './Todolist';





const App = ()=>{


const [inputlist ,setinputlist] = useState("");
const [items , setitems] = useState([]);

const itemEvent = (event) => {
setinputlist(event.target.value);
};

const clicktolist = () =>{
      setitems ((old_items_store_here)=>{
          return [...old_items_store_here,inputlist]
      });
      setinputlist('');
};


    return(
<>
 <div className="main_div">
<div className="center_div">
<br/>
<h1>to do list</h1>
<br/>
<input type="text" placeholder="Write here" onChange={itemEvent}  value={inputlist}/>
<button onClick={clicktolist}>+</button>
<ol>
    
    {items.map((itemsvalue)=>{
  
      return  <Todolist text={itemsvalue}></Todolist>;
    })}
</ol>
</div>
 </div>

</>
    )
};


export default App;
