import React, { createContext} from 'react';
import Context1 from "./Context1";
// import Todolist from './Todolist';
// import "../node_modules/bootstrap/dist/css/bootstrap.min.css";

const Firstname = createContext(); // this is context . in this way we create context
// we have to pass context as a components 
// and always pass other components inside the context 
//  to pass a value we want to write (value ={'write the value'})
// value keyword is complsory 
const App = ()=>{

return (
   <>
  <Firstname.Provider value={"krushna"}>
     <Context1/>  
     </Firstname.Provider>   
   </>
   );


};


export default App;
export {Firstname}; 
// to export more than 2 we have write inside the ' {} ' 
