import React from 'react';
import { Firstname } from "./App"; // firstname is inside the app compoents //


// Consumer only accept a function  . so any things that we have to access we have to write inside the function 
//  {(name)=>{}}  here name is parameter we access value from app in this name paramter 
// we can give any name to paramater; 

const Context3 = () =>{

    return( 
    <>
     <Firstname.Consumer> {(name)=>{
          return  <h1>my name is krushna  sunil bankar</h1>;
     }} 
      </Firstname.Consumer>
   
    </>
    );
};
export default Context3;
